import { useState , handleSubmit } from "react";
import React from "react";


const Form = () => {
  //Aqui deberan implementar el form completo con sus validaciones
	const [name, setName] = useState("")
	const [email, setEmail] = useState("")
	const [error, setError] = useState(false);

	const validateName = (name) => {
		const withoutSpaces = name.trim();

		if (withoutSpaces.length > 2) {
			return true;
			} else {
			return false;
			}
	}

	const onChangeName = (e) => setName(e.target.value)
	const onChangeEmail = (e) => setEmail(e.target.value)

	const handleSubmit = (e) => {
		e.preventDefault()

		const isUsernameValid = validateName(name);

		if(!isUsernameValid){
			setError(true)
		} else {

			console.log(`Gracias , ${name} te contactaremos cuando antes vía mail`);
		}

	}

	return (
		<div>
			<form
				onSubmit={handleSubmit}
			>
				<label htmlFor="name"></label>
				<input 
					type="text" 
					placeholder="Full Name"
					id="name"
					value={name}
					onChange={e => setName(e.target.value)}
				/>
				<input 
					type="email" 
					placeholder="Email"
					id="email"
					value={email}
					onChange={e => setEmail(e.target.value)}
				/>

				<input 
					type="submit" 
					value="Enviar"
				/>
			</form>
			{error && <p>Por favor chequea que la información sea correcta</p>}

		</div>
	);
};

export default Form;
