// Card.jsx
import React, { useContext } from "react";
import { Link } from "react-router-dom";
import { ContextGlobal } from "./utils/global.context";

const Card = ({ id }) => {
  const { state, actions } = useContext(ContextGlobal);

  const addFav = (id) => {
    actions.toggleFavorite(id);
  };

  // Find the corresponding user data for the card id
  const userData = state.data.find((user) => user.id === id);

  return (
    <div className={`card ${state.favorites.includes(id) ? 'favorite' : ''}`}>
      <div key={id}>
        <Link to={`/dentist/${id}`}>
          <img src="./images/doctor.jpg" alt="imagen-doctor" className="card" />
          <h3>{userData ? userData.name : 'Loading...'}</h3>
          <p>{userData ? userData.username : 'Loading...'}</p>
        </Link>
        <button onClick={() => addFav(id)} className="favButton">
          {state.favorites.includes(id) ? 'Remove from Favorites' : 'Add to Favorites'}
        </button>
      </div>
    </div>
  );
};

export default Card;
