import { createContext, useMemo, useReducer, useEffect, useState } from "react";

// Action types
const TOGGLE_THEME = "TOGGLE_THEME";
const ADD_TO_FAVS = "ADD_TO_FAVS";
const REMOVE_FROM_FAVS = "REMOVE_FROM_FAVS";

export const initialState = {
  theme: localStorage.getItem("theme") || "light",
  favs: [], // New state property for storing favs
};

const reducer = (state, action) => {
  switch (action.type) {
    case TOGGLE_THEME:
      return { ...state, theme: state.theme === "light" ? "dark" : "light" };
    case ADD_TO_FAVS:
      return { ...state, favs: [...state.favs, action.payload] };
    case REMOVE_FROM_FAVS:
      return { ...state, favs: state.favs.filter((id) => id !== action.payload) };
    default:
      return state;
  }
};

export const ContextGlobal = createContext(undefined);

export const ContextProvider = ({ children }) => {
  const [state, dispatch] = useReducer(reducer, initialState);

  const toggleTheme = () => {
    dispatch({ type: TOGGLE_THEME });
  };

  const addToFavs = (cardId) => {
    dispatch({ type: ADD_TO_FAVS, payload: cardId });
  };

  const removeFromFavs = (cardId) => {
    dispatch({ type: REMOVE_FROM_FAVS, payload: cardId });
  };

  useEffect(() => {
    localStorage.setItem("theme", state.theme);
  }, [state.theme]);

  const contextValue = useMemo(() => {
    return {
      state,
      actions: { toggleTheme, addToFavs, removeFromFavs },
    };
  }, [state]);

  return (
    <ContextGlobal.Provider value={contextValue}>
      {children}
    </ContextGlobal.Provider>
  );
};
