import { Link } from "react-router-dom";


const Card = ({ users , allUsers}) => {


  const addFav = (id) => {

    const favorites = JSON.parse(localStorage.getItem("favorites") || "[]");
    const existingFavorite = favorites.find((fav) => fav.id === id);


      if (existingFavorite) {
    const updatedFavorites = favorites.filter((fav) => fav.id !== id);
    localStorage.setItem("favorites", JSON.stringify(updatedFavorites));
  } else {
    // Add the card to favorites
    const user = allUsers.find((user) => user.id === id);
    if (user) {
      favorites.push(user);
      localStorage.setItem("favorites", JSON.stringify(favorites));
    }
  }
};

  return (
    <div className="container">
      {/* En cada card deberÃ¡n mostrar el nombre, el nombre de usuario y el id */}
      {users.map((user) => (
        <div key={user.id} id={user.id} className="card">
          <Link to={`/dentist/${user.id}`}>
            <img src="./images/doctor.jpg" alt="imagen-doctor" className="card" />
            <h3>{user.name}</h3>
            <p>{user.username}</p>
          </Link>
          <button onClick={() => addFav(user.id)} className="favButton">
            Add fav
          </button>
        </div>
      ))}
    </div>
  );
};

export default Card;
