import React, { useEffect, useState } from "react";
import { Link } from "react-router-dom";


const Card = ({ name, username, id }) => {
  const [allUsers, setAllUsers] = useState([])

  useEffect(() => {
    getAllUsers();
  }, [])

  const getAllUsers = async() => {
      const data = await
      fetch('https://jsonplaceholder.typicode.com/users')
      const convert = await data.json()
      console.log(data);
      setAllUsers(convert);
      console.log(setAllUsers);
  }

  const addFav = (id) => {
    // Get the existing favorites from the localStorage
    const favorites = JSON.parse(localStorage.getItem("favorites") || "[]");

    // Check if the current card id is already in the favorites
    const isFavorite = favorites.includes(id);

    // Add or remove the card from favorites based on its current state
    if (isFavorite) {
      // Remove the card from favorites
      const updatedFavorites = favorites.filter((favId) => favId !== id);
      localStorage.setItem("favorites", JSON.stringify(updatedFavorites));
    } else {
      // Add the card to favorites
      favorites.push(id);
      localStorage.setItem("favorites", JSON.stringify(favorites));
    }
  };

  return (
    <div>
      {/* En cada card deberÃ¡n mostrar el nombre, el nombre de usuario y el id */}
      {allUsers.map((user) => (
        <div key={user.id} className="card">
          <Link to={`/dentist/${user.id}`}>
            <img src="./images/doctor.jpg" alt="imagen-doctor" className="card" />
            <h3>{user.name}</h3>
            <p>{user.username}</p>
          </Link>
          <button onClick={() => addFav(user.id)} className="favButton">
            Add fav
          </button>
        </div>
      ))}
    </div>
  );
};

export default Card;
