// Card.jsx
import React, { useContext, useEffect, useState } from "react";
import { Link } from "react-router-dom";
import { ContextGlobal } from "./utils/global.context";

const Card = () => {
  const { state, actions } = useContext(ContextGlobal);
  const [allUsers, setAllUsers] = useState([]);

  useEffect(() => {
    getAllUsers();
  }, []);

  const getAllUsers = async () => {
    const data = await fetch('https://jsonplaceholder.typicode.com/users');
    const convert = await data.json();
    setAllUsers(convert);
  };

  const addFav = (userId) => {
    actions.addToFavs(userId);
  };

  const removeFav = (userId) => {
    actions.removeFromFavs(userId);
  };

  return (
    <div className="card">
      {allUsers.map((user) => (
        <div key={user.id}>
          <Link to={`/dentist/${user.id}`}>
            <img src="./images/doctor.jpg" alt="imagen-doctor" className="card" />
            <h3>{user.name}</h3>
            <p>{user.username}</p>
          </Link>
          {state.favs.includes(user.id) ? (
            <button onClick={() => removeFav(user.id)} className="favButton">
              Remove from favs
            </button>
          ) : (
            <button onClick={() => addFav(user.id)} className="favButton">
              Add to favs
            </button>
          )}
        </div>
      ))}
    </div>
  );
};

export default Card;
