import React, { useEffect, useState, useContext } from "react";
import { Link } from "react-router-dom";
import { ContextGlobal } from "./utils/global.context";

const Card = ({ name, username, id }) => {
  const [allUsers, setAllUsers] = useState([]);
  const { state, actions } = useContext(ContextGlobal) || {};

  useEffect(() => {
    getAllUsers();
  }, []);

  const getAllUsers = async () => {
    const data = await fetch("https://jsonplaceholder.typicode.com/users");
    const convert = await data.json();
    setAllUsers(convert);
  };

  // Check if the card with a specific ID is in favs
  const isCardInFavs = (cardId) => state.favs.includes(cardId);

  // Add or remove the card from favs
  const addFav = (cardId) => {
    if (!isCardInFavs(cardId)) {
      actions.addToFavs(cardId);
    } else {
      actions.removeFromFavs(cardId);
    }
  };

  return (
    <div className="card">
      {/* Displaying cards based on API data */}
      {allUsers.map((user) => (
        <div key={user.id}>
          {/* Utilizing Link to navigate to the page of a specific dentist */}
          <Link to={`/dentist/${user.id}`}>
            <img src="./images/doctor.jpg" alt="imagen-doctor" className="card" />
            <h3>{user.name}</h3>
            <p>{user.username}</p>
          </Link>

          {/* Button to add/remove the card from favs */}
          <button
            onClick={() => addFav(user.id)}
            className="favButton"
          >
            {/* Changing button text based on whether the card is in favs or not */}
            {isCardInFavs(user.id) ? "Remove from favs" : "Add fav"}
          </button>
        </div>
      ))}
    </div>
  );
};

export default Card;
