import React, { useContext, useEffect, useState } from "react";
import Card from "../Components/Card";
import { ContextGlobal } from "../Components/utils/global.context";

const Favs = () => {
  const { state } = useContext(ContextGlobal) || {};
  const [favCards, setFavCards] = useState([]);

  useEffect(() => {
    // Fetch fav cards based on favs stored in localStorage
    const fetchFavCards = async () => {
      const favCardsData = await Promise.all(
        state.favs.map(async (favId) => {
          const response = await fetch(
            `https://jsonplaceholder.typicode.com/users/${favId}`
          );
          return response.json();
        })
      );
      setFavCards(favCardsData);
    };

    fetchFavCards();
  }, [state.favs]);

  return (
    <div className={state?.theme === 'dark' ? 'dark-mode' : ''}>
      <h1>Dentists Favs</h1>
      <div className="card-grid">
        {/* Rendering cards based on the fetched fav cards */}
        {favCards.map((card) => (
          <Card key={card.id} {...card} />
        ))}
      </div>
    </div>
  );
};

export default Favs;
