import React, { useContext, useEffect } from "react";
import { Link } from "react-router-dom";
import { ContextGlobal } from "./utils/global.context";


const Card = ({user}) => {
  const { actions,  state } = useContext(ContextGlobal);


  const addFav = (id) => {
    actions.toggleFavorite(id);
};

useEffect(() => {
  const isFavorite = localStorage.getItem(`favorite_${user.id}`);
  if (isFavorite) {
    actions.setFavorite(user.id, true);
  }
}, [actions, user.id]);

// Effect to update localStorage when favorite status changes
useEffect(() => {
  if (user.isFavorite) {
    localStorage.setItem(`favorite_${user.id}`, "true");
  } else {
    localStorage.removeItem(`favorite_${user.id}`);
  }
}, [user.id, user.isFavorite]);

  return (
    <div className="container">
      {/* En cada card deber√°n mostrar el nombre, el nombre de usuario y el id */}
        <div key={user.id} id={user.id} className="card">
          <Link to={`/dentist/${user.id}`}>
            <img src="./images/doctor.jpg" alt="imagen-doctor" className="card" />
            <h3>{user.name}</h3>
            <p>{user.username}</p>
          </Link>
          <button onClick={() => addFav(user.id)} className="favButton">
            Add fav
          </button>
        </div>
    </div>
  );
};

export default Card;
