import React, { useContext } from "react";
import { Link } from "react-router-dom";
import { ContextGlobal } from "./utils/global.context";


const Card = ({user}) => {
  const { actions } = useContext(ContextGlobal);


  const addFav = (id) => {
    actions.toggleFavorite(id);
};

useEffect(() => {
  const favoritesString = localStorage.getItem("favorites");
  if (favoritesString) {
    const favorites = JSON.parse(favoritesString);
    if (favorites.includes(user.id)) {
      actions.setFavorite(user.id, true);
    }
  }
}, [actions, user.id]);

// Effect to update localStorage when favorite status changes
useEffect(() => {
  const favoritesString = localStorage.getItem("favorites");
  const favorites = favoritesString ? JSON.parse(favoritesString) : [];

  if (user.isFavorite && !favorites.includes(user.id)) {
    favorites.push(user.id);
  } else if (!user.isFavorite && favorites.includes(user.id)) {
    const index = favorites.indexOf(user.id);
    favorites.splice(index, 1);
  }

  localStorage.setItem("favorites", JSON.stringify(favorites));
}, [user.id, user.isFavorite]);


  return (
    <div className="container">
      {/* En cada card deber√°n mostrar el nombre, el nombre de usuario y el id */}
        <div key={user.id} id={user.id} className="card">
          <Link to={`/dentist/${user.id}`}>
            <img src="./images/doctor.jpg" alt="imagen-doctor" className="card" />
            <h3>{user.name}</h3>
            <p>{user.username}</p>
          </Link>
          <button onClick={() => addFav(user.id)} className="favButton">
            Favorite
          </button>
        </div>
    </div>
  );
};

export default Card;
