import React, { useContext, useEffect, useState } from "react";
import { ContextGlobal } from "../Components/utils/global.context";
import Card from "../Components/Card";

const Favs = () => {
  const { state } = useContext(ContextGlobal) || {};

  const [favoriteCards, setFavoriteCards] = useState([]);

  useEffect(() => {
    const favorites = JSON.parse(localStorage.getItem("favorites") || "[]");
    const filteredFavorites = favorites.filter(id => id !== null);
    setFavoriteCards(filteredFavorites);
  }, []);


  const addFavorite = (id) => {
    const favorites = JSON.parse(localStorage.getItem("favorites") || "[]");
    const updatedFavorites = [...favorites, id];
    localStorage.setItem("favorites", JSON.stringify(updatedFavorites));
    setFavoriteCards(updatedFavorites);
  };

  return (
    <div className={state?.theme === 'dark' ? 'dark-mode' : ''}>
      <h1>Dentists Favs</h1>
      <div className="card-grid">
        {/* este componente debe consumir los destacados del localStorage */}
        {/* Deberan renderizar una Card por cada uno de ellos */}
        {favoriteCards.map((favoriteId, index) => (
          <Card key={index} id={favoriteId} />
        ))}
      </div>
    </div>
  );
};

export default Favs;
