import React, { useContext, useEffect, useState } from "react";
import { Link } from "react-router-dom";
import { ContextGlobal } from "./utils/global.context";


const Card = ({ name, username, id }) => {

  const [allUsers, setAllUsers] = useState([])
	const { state, actions } = useContext(ContextGlobal);


  useEffect(() => {
    getAllUsers();
  }, [])


  const getAllUsers = async() => {
      const data = await
      fetch('https://jsonplaceholder.typicode.com/users')
      const convert = await data.json()
      console.log(data);
      setAllUsers(convert);
      console.log(setAllUsers);
  } 

	const isCardInFavs = (cardId) => state.favs.includes(cardId);


  const addFav = ()=>{
    // Aqui iria la logica para agregar la Card en el localStorage
		if (!isCardInFavs(allUsers.id)) {
      actions.addToFavs(allUsers.id);
    } else {
      actions.removeFromFavs(cardId);
    }
  }

  return (
    <div className="card">
      {/* En cada card deber치n mostrar el nombre, el nombre de usuario y el id */}
      {allUsers.map((user) => (
        <div key={user.id}>
          {/* Utilizar el Link para navegar a la p치gina de detalle */}
          <Link to={`/dentist/${user.id}`}>
            <img src="./images/doctor.jpg" alt="imagen-doctor" className="card" />
            <h3>{user.name}</h3>
            <p>{user.username}</p>
          </Link>

          {/* Utilizar el bot칩n para agregar a la p치gina de 'favs' */}
          <button onClick={() => alert(`${user.name}` + " Added to your favs")} className="favButton">
            Add fav
          </button>
        </div>
      ))}
    </div>
  );
};

export default Card;
