import React, { useContext, useEffect, useState } from "react";
import { Link } from "react-router-dom";
import { ContextGlobal } from "./utils/global.context";


const Card = ({ name, username, id }) => {

  const { state, actions } = useContext(ContextGlobal);

  const [allUsers, setAllUsers] = useState([]);

  useEffect(() => {
    getAllUsers();
  }, [])

  const getAllUsers = async() => {
      const data = await
      fetch('https://jsonplaceholder.typicode.com/users')
      const convert = await data.json()
      console.log(data);
      setAllUsers(convert);
      console.log(setAllUsers);
  }

  const addFav = (id) => {

    actions.toggleFavorite(id);
    }


    return (
      <div className={`card ${state.favorites.includes(id) ? 'favorite' : ''}`}>
        <div key={id}>
          <Link to={`/dentist/${id}`}>
            <img src="./images/doctor.jpg" alt="imagen-doctor" className="card" />
            <h3>{name}</h3>
            <p>{username}</p>
          </Link>
          <button onClick={() => addFav(id)} className="favButton">
            {state.favorites.includes(id) ? 'Remove from Favorites' : 'Add to Favorites'}
          </button>
        </div>
      </div>
    );
  };

export default Card;
