// Favs.js
import React, { useContext, useEffect, useState } from "react";
import { ContextGlobal } from "../Components/utils/global.context";
import Card from "../Components/Card";

const Favs = () => {
  const { state } = useContext(ContextGlobal) || {};

  const [allUsers, setAllUsers] = useState([]);

  useEffect(() => {
    getAllUsers();
  }, []);

  const getAllUsers = async () => {
    const data = await fetch('https://jsonplaceholder.typicode.com/users');
    const convert = await data.json();
    setAllUsers(convert);
    const favoriteUsers = convert.filter((user) => state.favorites.includes(user.id));
    console.log(favoriteUsers);
    console.log(state.favorites);

  };

  const favoriteUsers = allUsers.filter((user) => state.favorites.includes(user.id));

  return (
    <div className={state?.theme === 'dark' ? 'dark-mode' : ''}>
      <h1>Dentists Favs</h1>
      <div className="card-grid">
        <Card userssers={favoriteUsers} />
      </div>
    </div>
  );
};

export default Favs;
