import React, { useEffect, useState } from "react";
import { Link } from "react-router-dom";


const Card = ({ name, username, id }) => {
  const [allUsers, setAllUsers] = useState([])

  useEffect(() => {
    getAllUsers();
  }, [])

  const getAllUsers = async() => {
      const data = await
      fetch('https://jsonplaceholder.typicode.com/users')
      const convert = await data.json()
      console.log(data);
      setAllUsers(convert);
      console.log(setAllUsers);
  }

  const addFav = (id) => {

    const favorites = JSON.parse(localStorage.getItem("favorites") || "[]");

  // Check if the current card id is already in the favorites
    const existingFavorite = favorites.find((fav) => fav.id === id);

  // Add or remove the card from favorites based on its current state
      if (existingFavorite) {
    // Remove the card from favorites
    const updatedFavorites = favorites.filter((fav) => fav.id !== id);
    localStorage.setItem("favorites", JSON.stringify(updatedFavorites));
  } else {
    // Add the card to favorites
    const user = allUsers.find((user) => user.id === id);
    if (user) {
      favorites.push(user);
      localStorage.setItem("favorites", JSON.stringify(favorites));
    }
  }
};

return (
  <div className="container">
    {/* Each card should display the name, username, and id */}
    <div key={user.id} id={user.id} className="card">
      <Link to={`/dentist/${user.id}`}>
        <img src="./images/doctor.jpg" alt="imagen-doctor" className="card" />
        {/* Display user information */}
        <h3>{user.name}</h3>
        <p>{user.username}</p>
      </Link>
      <button onClick={() => addFav(user.id)} className="favButton">
        Add fav
      </button>
    </div>
  </div>
);
};

export default Card;
